DROP TABLE IF EXISTS `ccc_chains`;
CREATE TABLE `ccc_chains`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '特定ID',
  `name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '院线简称',
  `full_name` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '院线全称',
  `tribe` tinyint(4) NULL DEFAULT NULL COMMENT '院线类别，1：一级市场院线，2：二级市场非院线',
  `user_id` bigint(20) NULL DEFAULT NULL COMMENT '对应登录账号id,可为空',
  `chain_address` json NULL COMMENT '{\"sheng\":110000000,\"shi\":110100000,\"xian\":110101000,\"zhen\":110101001,\"city\":110000000,\"sheng_s\":\"xx\",\"shi_s\":\"xx\",\"xian_s\":\"xx\",\"zhen_s\":\"xx\",\"street\":\"xxx\"}',
  `sheng` int(11) GENERATED ALWAYS AS (json_unquote(json_extract(`chain_address`,'$.sheng'))) VIRTUAL NULL,
  `shi` int(11) GENERATED ALWAYS AS (json_unquote(json_extract(`chain_address`,'$.shi'))) VIRTUAL NULL,
  `xian` int(11) GENERATED ALWAYS AS (json_unquote(json_extract(`chain_address`,'$.xian'))) VIRTUAL NULL,
  `address` varchar(900) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci GENERATED ALWAYS AS (concat(json_unquote(json_extract(`chain_address`,'$.sheng_s')),' ',json_unquote(json_extract(`chain_address`,'$.shi_s')),' ',json_unquote(json_extract(`chain_address`,'$.xian_s')),' ',json_unquote(json_extract(`chain_address`,'$.zhen_s')),' ',json_unquote(json_extract(`chain_address`,'$.street')))) VIRTUAL NULL,
  `phone` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '联系电话',
  `fax` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '传真',
  `email` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '电子邮箱',
  `last_modified` timestamp(0) NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '硬盘数量',
  `duty_person` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '院线负责人，形如  \".1.2.3.\"',
  `status` int(11) NULL DEFAULT 1 COMMENT '院线状态：0未入院线，1正常，2异常',
  `invalid` int(11) NULL DEFAULT 0 COMMENT '标记删除：0：不删 ，1标删',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 230009 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '院线表' ROW_FORMAT = Dynamic;

