DROP TABLE IF EXISTS `ccc_chains_address`;
CREATE TABLE `ccc_chains_address`  (
  `address_id` bigint(20) NOT NULL AUTO_INCREMENT,
  `recipient_name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '收件人姓名',
  `recipient_phone` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '收件人电话',
  `province_order` int(11) NULL DEFAULT 0 COMMENT '省份排序',
  `province_name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
  `post_address` json NULL COMMENT '{\"sheng\":110000000,\"shi\":110100000,\"xian\":110101000,\"zhen\":110101001,\"city\":110000000,\"sheng_s\":\"xx\",\"shi_s\":\"xx\",\"xian_s\":\"xx\",\"zhen_s\":\"xx\",\"street\":\"xxx\"}',
  `sheng` int(20) GENERATED ALWAYS AS (json_extract(`post_address`,'$.sheng')) VIRTUAL NULL,
  `shi` int(20) GENERATED ALWAYS AS (json_extract(`post_address`,'$.shi')) VIRTUAL NULL,
  `xian` int(20) GENERATED ALWAYS AS (json_extract(`post_address`,'$.xian')) VIRTUAL NULL,
  `zhen` int(20) GENERATED ALWAYS AS (json_extract(`post_address`,'$.zhen')) VIRTUAL NULL,
  `sheng_name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci GENERATED ALWAYS AS (json_unquote(json_extract(`post_address`,'$.sheng_s'))) VIRTUAL NULL,
  `shi_name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci GENERATED ALWAYS AS (json_unquote(json_extract(`post_address`,'$.shi_s'))) VIRTUAL NULL,
  `xian_name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci GENERATED ALWAYS AS (json_unquote(json_extract(`post_address`,'$.xian_s'))) VIRTUAL NULL,
  `zhen_name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci GENERATED ALWAYS AS (json_unquote(json_extract(`post_address`,'$.zhen_s'))) VIRTUAL NULL,
  `street` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci GENERATED ALWAYS AS (json_unquote(json_extract(`post_address`,'$.street'))) VIRTUAL NULL,
  `disk_count` int(10) NULL DEFAULT 0 COMMENT '邮寄硬盘数',
  `cinema` int(10) NULL DEFAULT NULL COMMENT '影院编码',
  `cinema_name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '影院名称',
  `chain` int(10) NULL DEFAULT NULL COMMENT '院线编号',
  `chain_name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '院线名称',
  `target_type` tinyint(4) NULL DEFAULT 2 COMMENT '邮寄地址类型，1院线，2影院，3其他',
  `target_name` varchar(300) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '邮寄对象名称',
  `removed` int(11) NULL DEFAULT 0 COMMENT '是否标记删除 0正常，1删除',
  `used` int(11) NULL DEFAULT 0 COMMENT '是否使用额外地址 0 不使用 1 使用',
  `receiver_way` int(11) NULL DEFAULT 1 COMMENT '1 邮寄 2自取 3委托',
  `chain_address` json NULL COMMENT '院线地址',
  `dock` int(11) NOT NULL DEFAULT 10 COMMENT '默认发运点：10=》北京发，11=〉上海发',
  PRIMARY KEY (`address_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 54 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '硬盘递运详细列表' ROW_FORMAT = Dynamic;

