DROP TABLE IF EXISTS `fdm_project`;
CREATE TABLE `fdm_project`  (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '项目同步ID',
  `create_source` int(10) NOT NULL DEFAULT 1 COMMENT '项目来源：1：同步，2：自建',
  `pid` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '项目编号，按照用户指定的编码规则',
  `pid_number` bigint(20) UNSIGNED NOT NULL COMMENT '排序专用id',
  `ptype` int(10) NOT NULL DEFAULT 0 COMMENT '项目类型：0：未知，1：普通，2：艺术院线，3历史数据，99：测试项目',
  `pclass` int(10) NOT NULL DEFAULT 0 COMMENT '项目分类：0：未知，1：进口，2：国产',
  `pfamily` int(10) NOT NULL DEFAULT 0 COMMENT '项目科类：0：未知，1：分账，2：买断',
  `online` int(10) NOT NULL DEFAULT 0 COMMENT '是否上线开启点映，0：未开启，1：开启',
  `limit_times` int(10) NOT NULL DEFAULT 1 COMMENT '每影院放映次数限制',
  `name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '名称',
  `check_status` tinyint(4) NOT NULL DEFAULT 0 COMMENT '项目审核状态：0未提交、1待审核、2未通过、3已通过',
  `settlement_status` int(10) NULL DEFAULT 0 COMMENT '结算状态：0未完成，1结算完成',
  `settlement_chain` mediumtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '院线结算数据：json格式：{remark,document:[{doc_type:6,doc_names:,doc_id,origin_name},,,,,]}',
  `settlement_producer` mediumtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '片方结算数据：json格式：{settlement_time:{publish_start:publish_end , months:3},settlement_object[{name,bank,account_name,bank_account},{}],server_fee:{},agent_fee:{}}',
  `settlement_import_export` mediumtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '进出口结算数据：json格式：{imex_rate:100}',
  `digital_product_fee` bigint(20) NULL DEFAULT 0 COMMENT '数字制作费，单位：分',
  `digital_copy_fee` bigint(20) NULL DEFAULT 0 COMMENT '数字制作复制费，单位：分',
  `digital_other_fee` bigint(20) NULL DEFAULT 0 COMMENT '其他外部采购等费用，单位：分',
  `duty_person_array` json NULL COMMENT '项目责任人复数',
  `duty_person_idx` json NULL COMMENT '项目责任人复数索引',
  `duty_person` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '项目认领人(客户经理)姓名',
  `duty_person_id` int(11) NULL DEFAULT NULL COMMENT '项目认领人(客户经理account_manager)用户ID',
  `hzdw_array` json NULL COMMENT '合作单位复数',
  `hzdw_idx` json NULL COMMENT '合作单位复数索引',
  `movie_producer` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '查看票房的用户账号json数据',
  `project_producer` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '公映许可证上的出品单位。',
  `authorize_start_time` datetime(0) NULL DEFAULT NULL COMMENT '项目授权开始时间',
  `authorize_end_time` datetime(0) NULL DEFAULT NULL COMMENT '项目授权结束时间',
  `release_date` date NULL DEFAULT NULL COMMENT '上映时间',
  `zone` tinyint(4) NULL DEFAULT NULL COMMENT '甲乙区信息，分区 0:全国, 1:甲区, 2:乙区',
  `publish_start_time` datetime(0) NULL DEFAULT NULL COMMENT '首映日',
  `publish_end_time` datetime(0) NULL DEFAULT NULL COMMENT '落片日',
  `publish_notice` mediumtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '发行通知：[{start_time,end_time,upload_time,doc_type:6,doc_names:,doc_id,origin_name},,,]',
  `investment` mediumtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '投资信息：[publish:{amount,remark,settlement},company:{}]',
  `investment_company` mediumtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '投资公司投资信息{company:{partner,movie_cost,invest_amount,invest_rate,}}',
  `contract` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '合同数据[{doc_type:6,doc_names:,doc_id,origin_name},,,,,]',
  `permits` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '许可证[{doc_type:6,doc_names:,doc_id,origin_name},,,,,]',
  `movie_id` int(11) NULL DEFAULT NULL COMMENT '电影ID',
  `movie_name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '电影名称',
  `origin` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '国别',
  `cooperation_mode` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '项目类型，{\"F\":\"分账\",\"M\":\"买断\",\"GF\":\"国产\",\"YS\":\"艺术\",\"GT\":\"投资\",\"JC\":\"监察\"}',
  `last_modify` timestamp(0) NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '最后修改时间，请勿修改',
  `os_create_time` datetime(0) NULL DEFAULT NULL COMMENT '外部系统声称的创建日期',
  `os_update_time` datetime(0) NULL DEFAULT NULL COMMENT '外部系统声称的修改时间',
  `invalid` bigint(20) NOT NULL DEFAULT 0 COMMENT '标记删除',
  `mos_pname` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '项目名称',
  `mos_is_supervision` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '是否监察（1：是，2：否）	null没填',
  `mos_status` int(10) NULL DEFAULT NULL COMMENT '执行情况，0:未执行、1：执行中，2：已完结，3：已终止',
  `mos_ptype` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '影片类项目类型',
  `mos_is_plan` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '是否计划内监察',
  `mos_note` varchar(600) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '备注',
  `mos_country` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '国家/地区：国产、非美、港台',
  `mos_is_target` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '是否指标项0：非指标项，1：指标项',
  `mos_index_year` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '指标年份',
  `mos_tz_type` varchar(1000) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '合作类型',
  `mos_company` varchar(1000) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '片商',
  `mos_scheduled` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '上映档期',
  `mos_zhongying` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '中影 甲乙区',
  `mos_huaxia` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '华夏 甲乙区',
  `mos_translate` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '译制厂',
  `mos_scale` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '荧幕比',
  `mos_trading_fzr` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '进出口联系人',
  `mos_edition` varchar(300) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '版本类别',
  `mos_nation` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '国别	国产/进口',
  `mos_hzdw` varchar(600) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '合作单位',
  `mos_ratio` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '票房分成比例',
  `mos_consignor` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '委托方',
  `is_market` tinyint(4) NULL DEFAULT 0 COMMENT '是否二级市场',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `code`(`pid`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 152782126472597 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '项目表' ROW_FORMAT = Dynamic;

