INSERT INTO `fdm_project` VALUES (1481509842254, 1, 'M2016018', 2016018, 1, 1, 2, 0, 1, '了不起的菲丽西', 0, 0, '{\"remark\":\"\"}', '{\"settlement_time\": {\"start_time\": \"2017-03-31\", \"months\": \"3\"}, \"settlement_object\": [{\"index\": \"1\", \"object_name\": \"结算对象一\", \"bank\": \"中国工商银行股份有限公司天津中新生态城支行\", \"account_name\": \"海秀传媒（天津）有限公司\", \"bank_account\": \"0302 0933 0910 0076 220\"}], \"service_fee\": {\"boxoffice_type\": \"净票房\", \"distribute_object\": \"片方分成\", \"function_name\": \"producer_step_distribution\", \"data\": {\"base_amount\": \"20000000\", \"base_rate\": \"1500\", \"top_amount\": \"110000000\", \"top_rate\": \"2500\", \"step_add_amount\": \"10000000\", \"step_add_rate\": \"100\"}}, \"agent_fee\": {\"contract_text\": \"三、该片在院线、影院的发行收入以及总政、武警、数字流动放映等的销售收入全部汇入甲方账户。该片在院线、影院发行的收入分配方式按本协议第五条分配。总政、武警、数字流动放映等的销售收入按甲方得10%，乙方得90%进行分配。 \\n四、乙方应在与甲方签订原合同之日起2个工作日内，一次性向甲方预付如下款项：\\n版权费、税金、手续费、素材检验费、送审费、票房分成预付款（见“《了不起的菲丽西》宣发预算”），共计人民币叁拾伍万元整（￥350,000.00 ）\\n五、甲、乙双方同意，乙方按如下方式分得该片的院线、影院放映票房（以下简称“总票房”或“票房总收入”）的发行收入：\\n当该片总票房在人民币2000万元以内时，乙方分得该片净票房的15%；当该片总票房在人民币2000万元（含2000万元）-3000万元(不含3000万元)时，乙方分得该片净票房的16%；以此类推，该片总票房每增加1000万元，乙方分账比例总体增加1%；当该片总票房达到人民币1.1亿元（含1.1亿元）时，乙方分得该片净票房的25%，该比例为乙方分账比例的最高限，乙方分账比例不再随总票房增加而增加。\\n六、甲、乙双方确认，中国电影集团公司电影进出口分公司（以下简称“进出口公司”）获得该片票房总收入1.5%的分成，此项费用由乙方承担，从乙方应得收入中先行扣除。\\n七、甲、乙双方同意，双方按以下方式进行影片结算：\\n（1）乙方按本协议第五条约定分得的该片院线、影院放映发行收入首先扣除实际应支付进出口公司的各项费用（包括但不限于进出口公司应得的版权费、税金、手续费、素材检验费、送审费、票房分成预付款）；其次扣除甲方垫付的费用（详见《了不起的菲丽西》宣发预算第二、三项）超出甲方实际应承担的宣发费用部分（如有）。\\n（2）甲方实际应承担的宣发费用（详见本协议第九条约定），该项费用800万元封顶；\\n（3）若乙方应得的该片发行收入足以抵扣本条第（1）款约定的应扣除的各项费用，甲方返还乙方支付的预付款至乙方指定账户；若乙方应得的该片发行收入不足以抵扣上述相关款项，甲方将从乙方支付的预付款中进行相应扣除，剩余预付款（如有）返还至乙方指定账户；若乙方预付款仍不足以抵扣上述相关款项，乙方应在收到甲方的书面通知之日起的五个工作日内补齐差额。逾期不补的，每逾一日，乙方应向甲方支付应付而未付金额的5‰的违约金，直至应付而未付金额付清为止。\"}, \"digital_fee\": {\"payway\": {\"pkdk\": \"1\", \"pfzf\": \"1\"}, \"sum_digital_fee_1\": \"28040000\", \"sum_digital_fee_2\": \"81984000\", \"sum_digital_fee_3\": 0, \"docs\": [{\"doc_id\": \"1078\", \"doc_type\": \"12\", \"origin_name\": 