INSERT INTO `fdm_project` VALUES (1487839953270, 1, 'M2017001', 2017001, 1, 1, 2, 0, 1, '摔跤吧！爸爸', 0, 0, '{\"remark\":\"\"}', '{\"settlement_time\": {\"start_time\": \"2017-07-04\", \"months\": \"3\"}, \"settlement_object\": [{\"index\": \"1\", \"object_name\": \"结算对象一\", \"bank\": \"中国民生银行北京亚运村支行限公司\", \"account_name\": \"北京创世星国际影视文化有限公司\", \"bank_account\": \"699523519\", \"remark\": \"分账款\"}, {\"index\": \"2\", \"object_name\": \"结算对象二\", \"bank\": \"中国工商银行北京北太平庄支行有限公司\", \"account_name\": \"北京创世星国际影视文化有限公司\", \"bank_account\": \"0200010009200190336\", \"remark\": \"退预付款\"}], \"service_fee\": {\"boxoffice_type\": \"总票房\", \"distribute_object\": \"总票房\", \"function_name\": \"总票房\"}, \"agent_fee\": {\"contract_text\": \"三、该片在院线、影院的发行收入以及总政、武警、数字流动放映等的销售收入全部汇入甲方账户。该片在院线、影院发行的收入分配方式按本协议第六条分配，总政、武警、数字流动放映等的销售收入按甲方得10%，乙方得90%进行分配。\\n四、乙方应在与甲方签订原合同之日起5个工作日内，一次性向甲方预付如下款项：\\n版权费、各项税金、对外付款手续费、票房分成款预付款、译制费、声画合成费、数字母版制作费、硬盘复制及发运费、保底收益（见“《摔跤吧！爸爸》发行预算”），共计人民币叁佰玖拾伍万元整（￥3,950,000.00）（以下简称“预付款”）支付至甲方的指定账户，账户信息如下：\\n开户名称：华夏电影发行有限责任公司\\n    开户银行：中国建设银行北京北太平庄支行\\n账    号：1100 1016 5000 5300 4211 \\n乙方逾期支付上述款项的，每逾一日，乙方应向甲方支付应付而未付金额的5‰的违约金。\\n    五、甲、乙双方同意，双方就该片的投入以各自加盖公司公章的结算清单及发票原件为准。\\n六、甲、乙双方同意，该片的院线、影院放映票房（以下简称“总票房”或“票房总收入”）的发行收入按以下方式分配：\\n     1、当该片总票房在人民币3000万元以内（含3000万元）时，乙方保证甲方就该片所得的保底收益为人民币300万元；\\n     2、当该片总票房超过人民币3000万元、低于人民币1亿元（含1亿元）时，甲方在保底收益300万元的基础上，再收取超出3000万元部分票房的5%；\\n     3、当该片总票房超过人民币1亿元时，甲方再收取超出1亿元部分票房的10%。  \\n   七、甲、乙双方确认，中国电影集团公司电影进出口分公司（以下简称“进出口公司”）获得该片票房总收入1.5%的分成。\\n    八、甲、乙双方同意，根据本协议第三条约定汇入甲方的发行收入按以下顺序回收和分配：\\n（1）首先抵扣甲方的成本及甲方应得的发行收益；\\n（2）再扣除进出口公司应得的票房分成款；\\n（3）剩余发行收入支付至乙方指定的银行账户。\\n    若该片的发行收入足以抵扣甲方的成本、甲方应得的发行收益及进出口公司应得的票房分成款等相关款项，甲方返还乙方支付的预付款至乙方指定账户；如该片发行收入不足以抵扣上述相关款项，甲方将从乙方支付的预付款中进行相应扣除，剩余预付款（如有）返还至乙方指定账户；如乙方预付款仍不足以抵扣上述相关款项，乙方应在收到甲方的书面通知之日起的五个工作日内补齐差额。逾期不补的，每逾一日，乙方应向甲方支付应付而未付金额的5‰的违约金，直至应付而未付金额付清为止。\\n    九、该片的发行收入，在扣除甲方的成本、甲方应得的发行收益及进出口公司应得的票房分成款后余额支付至乙方指定账户，账户信息如下:\\n开户名称：北京创世星国际影视文化有限公司\\n开户银行：中国民生银行北京亚运村支行\\n账    号：699523519\\n乙方应于甲方付款前向甲方开具乙方应得收入等额的合法发票，若乙方未按约定交付发票，甲方有权延迟履行付款义务，且不承担任何违约责任。\"}, \"digital_fee\": {\"payway\": {\"pkdk\": \"1\"}, \"sum_digital_fee_1\": \"13590000\", \"sum_digital_fee_2\": \"36409500\", \"sum_digital_fee_3\": 0, \"docs\": [{\"doc_id\": \"2707\", \"doc_type\": \"12\", \"origin_name\": \"摔跤吧！爸爸 数字清单 盖章.JPG\", \"doc_name\": \"sgdry10vatspcum2.jpeg\", \"thumbs\": [\"sgdry10vatspcum2.jpeg\"], \"catalog\": \"prjdoc\", \"type\": \"image\"}]}}', '{\"imex_rate\":\"150\",\"advance_pay\":\"200000\",\"buyout_pay\":\"140000\",\"material_fee\":\"0\"}', 100000, 35658000, 0, '[\"崔松雪\", \"金淑云\"]', '{\"UID87\": \"崔松雪\", \"UID89\": \"金淑云\"}', '崔松雪', 87, '[\"HX-PF-13\"]', '{\"HX-PF-13\": \"北京创世星国际影视文化有限公司\"}', '[{\"user_id\":\"9038\",\"user_name\":\"u521bu4e16u661f\"}]', '{\"372\":\"印度UTV影片公司\",\"373\":\"印度阿米尔·汗影片公司\"}', '2017-04-18 00:00:00', '2019-04-18 00:00:00', '2017-05-05', 0, '2017-04-30 00:00:00', '2017-07-04 00:00:00', '[{\"start_time\": \"2017-05-05\", \"end_time\": \"2017-06-04\", \"docs\": [{\"doc_id\": \"799\", \"doc_type\": \"5\", \"origin_name\": \"《摔跤吧！爸爸》发行通知.pdf\", \"doc_name\": \"kg9uhsevc4663d7s.pdf\", \"thumbs\": [\"8c9lbl096vfwbgf6.jpeg\", \"2rtkrvctstx4z06e.jpeg\", \"1e5rjje8ialyacf7y.jpeg\"], \"catalog\": \"prjdoc\", \"type\": \"pdf\"}]}, {\"start_time\": \"2017-04-30\", \"end_time\": \"2017-04-30\", \"docs\": [{\"doc_id\": \"811\", \"doc_type\": \"5\", \"origin_name\": \"摔跤吧！爸爸 点映通知.JPG\", \"doc_name\": \"g2b1q8j89rmqa2og.jpeg\", \"thumbs\": [\"g2b1q8j89rmqa2og.jpeg\"], \"catalog\": \"prjdoc\", \"type\": \"image\"}]}, {\"start_time\": \"2017-06-05\", \"end_time\": \"2017-07-04\", \"docs\": [{\"doc_id\": \"1009\", \"doc_type\": \"5\", \"origin_name\": \"密钥延期通知.jpg\", \"doc_name\": \"tfo5lubtekjf9ayb.jpeg\", \"thumbs\": [\"tfo5lubtekjf9ayb.jpeg\"], \"catalog\": \"prjdoc\", \"type\": \"image\"}]}]', NULL, NULL, '[{\"doc_id\": \"803\", \"doc_type\": \"6\", \"origin_name\": \"摔跤吧！爸爸 协推合同及补充协议.pdf\", \"doc_name\": \"1fev68fnvrrmruwtk.pdf\", \"thumbs\": [\"wo0nwkmxmfbg9oq2.jpeg\", \"