INSERT INTO `fdm_project` VALUES (1489111628285, 1, 'M2017004', 2017004, 1, 1, 2, 0, 1, '守护者：世纪战元', 0, 0, '{\"remark\":\"\"}', '{\"settlement_time\": {\"start_time\": \"2017-05-19\", \"months\": \"3\"}, \"settlement_object\": [{\"index\": \"1\", \"object_name\": \"结算对象一\", \"bank\": \"福建海峡银行闽清支行\", \"account_name\": \"世纪长龙影视有限公司\", \"bank_account\": \" 1000 0114 8040 0100 05\", \"remark\": \"\"}], \"service_fee\": {\"boxoffice_type\": \"总票房\", \"distribute_object\": \"总票房\", \"function_name\": \"总票房\"}, \"agent_fee\": {\"contract_text\": \"三、该片在院线、影院的发行收入以及总政、武警、数字流动放映等的销售收入全部汇入甲方账户。该片在院线、影院发行的收入分配方式按本协议第六条分配，总政、武警、数字流动放映等的销售收入按甲方得10%，乙方得90%进行分配。\\n六、甲、乙双方同意，该片的院线、影院放映票房（以下简称“总票房”或“票房总收入”）的发行收入按以下方式分配：\\n     1、当该片总票房在人民币3000万元以内（含3000万元）时，乙方保证甲方就该片所得的保底收益为人民币300万元；\\n     2、当该片总票房超过人民币3000万元、低于人民币1亿元（含1亿元）时，甲方在保底收益300万元的基础上，再收取超出3000万元部分票房的5%；\\n     3、当该片总票房超过人民币1亿元时，甲方再收取超出1亿元部分票房的10%。  \\n 七、甲、乙双方确认，中国电影集团公司电影进出口分公司（以下简称“进出口公司”）获得该片票房总收入1.5%的分成。\\n    八、甲、乙双方同意，根据本协议第三条约定汇入甲方的发行收入按以下顺序回收和分配：\\n（1）首先抵扣甲方的成本及甲方应得的发行收益；\\n（2）再扣除进出口公司应得的票房分成款；\\n（3）剩余发行收入支付至乙方指定的银行账户。\\n    若该片的发行收入足以抵扣甲方的成本、甲方应得的发行收益及进出口公司应得的票房分成款等相关款项，甲方返还乙方支付的预付款至乙方指定账户；如该片发行收入不足以抵扣上述相关款项，甲方将从乙方支付的预付款中进行相应扣除，剩余预付款（如有）返还至乙方指定账户；如乙方预付款仍不足以抵扣上述相关款项，乙方应在收到甲方的书面通知之日起的五个工作日内补齐差额。逾期不补的，每逾一日，乙方应向甲方支付应付而未付金额的5‰的违约金，直至应付而未付金额付清为止。\"}, \"digital_fee\": {\"payway\": {\"pkdk\": \"1\"}, \"sum_digital_fee_1\": \"20340000\", \"sum_digital_fee_2\": \"54622500\", \"sum_digital_fee_3\": 0, \"docs\": [{\"doc_id\": \"2092\", \"doc_type\": \"12\", \"origin_name\": \"守护者：世纪战元 数字制作清单.jpg\", \"doc_name\": \"6w2zsmjw9s6ufiqm.jpeg\", \"thumbs\": [\"6w2zsmjw9s6ufiqm.jpeg\"], \"catalog\": \"prjdoc\", \"type\": \"image\"}]}}', '{\"imex_rate\":\"150\",\"advance_pay\":\"200000\",\"buyout_pay\":\"140000\",\"material_fee\":\"0\"}', 460000, 54622500, 0, '[\"崔松雪\"]', '{\"UID87\": \"崔松雪\"}', '崔松雪', 87, '[\"PF000017\"]', '{\"PF000017\": \"世纪长龙影视有限公司\"}', '[{\"user_id\":\"7087\",\"user_name\":\"u4e16u7eaau957fu9f99\"}]', '{\"388\":\"俄