INSERT INTO `fdm_project` VALUES (1491977397922, 1, 'M2017009', 2017009, 1, 1, 2, 0, 1, '深夜食堂2', 0, 0, '{\"remark\":\"\"}', '{\"settlement_time\": {\"start_time\": \"2017-08-17\", \"months\": \"3\"}, \"settlement_object\": [{\"index\": \"1\", \"object_name\": \"结算对象一\", \"bank\": \"中国银行股份有限公司北京燕莎中心支行\", \"account_name\": \"双喜电影发行（北京）有限公司\", \"bank_account\": \"323365637458\", \"remark\": \"\"}], \"service_fee\": {\"boxoffice_type\": \"总票房\", \"distribute_object\": \"华夏分成\", \"function_name\": \"huaxia_split_distribution\", \"data\": {\"split_amount\": \"100000000\", \"left_rate\": \"1000\", \"right_rate\": \"1200\"}}, \"agent_fee\": {\"contract_text\": \"三、该片在院线、影院的发行收入以及总政、武警、数字流动放映等的销售收入全部汇入甲方账户。该片在院线、影院发行的收入分配方式按本协议第六条分配，总政、武警、数字流动放映等的销售收入按甲方得10%，乙方得90%进行分配。\\n六、甲、乙双方同意，该片的院线、影院放映票房（以下简称“总票房”或“票房总收入”）的发行收入按以下方式分配：\\n     1、当该片总票房在人民币1亿元以内（含1亿元）时，甲方收取总票房的10%；\\n     2、当该片总票房大于人民币1亿元时，甲方再收取超出1亿元部分票房的12%。  \\n   七、甲、乙双方确认，中国电影集团公司电影进出口分公司（以下简称“进出口公司”）获得该片票房总收入1.5%的分成。\\n    八、甲、乙双方同意，根据本协议第三条约定汇入甲方的发行收入按以下顺序回收和分配：\\n（1）首先抵扣甲方的成本及甲方应得的发行收益；\\n（2）再扣除进出口公司应得的票房分成款；\\n（3）剩余发行收入支付至乙方指定的银行账户。\\n    若该片的发行收入足以抵扣甲方的成本、甲方应得的发行收益及进出口公司应得的票房分成款等相关款项，甲方返还乙方支付的预付款至乙方指定账户；如该片发行收入不足以抵扣上述相关款项，甲方将从乙方支付的预付款中进行相应扣除，剩余预付款（如有）返还至乙方指定账户；如乙方预付款仍不足以抵扣上述相关款项，乙方应在收到甲方的书面通知之日起的五个工作日内补齐差额。逾期不补的，每逾一日，乙方应向甲方支付应付而未付金额的5‰的违约金，直至应付而未付金额付清为止。\"}, \"digital_fee\": {\"payway\": {\"pkdk\": \"1\"}, \"sum_digital_fee_1\": \"12430000\", \"sum_digital_fee_2\": \"34330500\", \"sum_digital_fee_3\": 0, \"docs\": [{\"doc_id\": \"2244\", \"doc_type\": \"12\", \"origin_name\": \"数字制作费用清单.jpg\", \"doc_name\": \"j5zuohnbcw2gbqa1.jpeg\", \"thumbs\": [\"j5zuohnbcw2gbqa1.jpeg\"], \"catalog\": \"prjdoc\", \"type\": \"image\"}]}}', '{\"imex_rate\":\"150\",\"advance_pay\":\"200000\",\"buyout_pay\":\"140000\",\"material_fee\":\"0\"}', 100000, 34330500, 0, '[\"崔松雪\"]', '{\"UID87\": \"崔松雪\"}', '崔松雪', 87, '[\"PF000224\"]', '{\"PF000224\": \"双喜电影发行（北京）有限公司\"}', NULL, '{\"596\":\"日本东映株式会社\"}', '2017-06-06 00:00:00', '2020-06-