INSERT INTO `fdm_project` VALUES (1499654181207, 1, 'M2017016', 2017016, 1, 1, 2, 0, 1, '火力全开', 0, 0, '{\"remark\":\"\"}', '{\"settlement_time\": {\"start_time\": \"2017-10-20\", \"months\": \"3\"}, \"settlement_object\": [{\"index\": \"1\", \"object_name\": \"结算对象一\", \"bank\": \"交通银行北京东区支行\", \"account_name\": \"北京耀莱影视文化传媒有限公司\", \"bank_account\": \"110060973018010097703\", \"remark\": \"\"}], \"service_fee\": {\"boxoffice_type\": \"发行收入\", \"distribute_object\": \"华夏分成\", \"function_name\": \"huaxia_rate_distribution\", \"data\": {\"huaxia_rate\": \"800\"}}, \"agent_fee\": {\"contract_text\": \"二、甲方负责向相关公司支付乙方预付的该片版权和素材购买费用、相关引进费用、票房分成款预付款。甲方负责该片数字母版制作、硬盘复制及发运，费用由乙方承担。乙方承担全部宣传推广、发行差旅等费用。\\n三、该片在院线、影院的发行收入全部汇入甲方账户。甲方收取该片院线、影院发行收入的8%。\\n四、乙方应在与甲方签订原合同之日起5个工作日内，一次性向甲方预付如下款项：\\n版权费、各项税金、对外付款手续费、票房分成款预付款、数字母版制作费、硬盘复制及发运费用（见“《火力全开》发行预算”），共计人民币壹佰叁拾万元整（￥1,300,000.00）（以下简称“预付款”）支付至甲方的指定账户，账户信息如下：\\n开户名称：华夏电影发行有限责任公司\\n    开户银行：中国建设银行北京北太平庄支行\\n账    号：1100 1016 5000 5300 4211 \\n乙方逾期支付上述款项的，每逾一日，乙方应向甲方支付应付而未付金额的5‰的违约金。\\n    五、甲、乙双方同意，双方就该片的投入以各自加盖公司公章的结算清单及发票原件为准。\\n   六、甲、乙双方确认，中国电影集团公司电影进出口分公司（以下简称“进出口公司”）获得该片票房总收入1.5%的分成。\\n    七、甲、乙双方同意，根据本协议第三条约定汇入甲方的发行收入按以下顺序回收和分配：\\n（1）首先抵扣甲方的成本及甲方应得的发行收益；\\n（2）再扣除进出口公司应得的票房分成款；\\n（3）剩余发行收入支付至乙方指定的银行账户。\\n    若该片的发行收入足以抵扣甲方的成本、甲方应得的发行收益及进出口公司应得的票房分成款等相关款项，甲方返还乙方支付的预付款至乙方指定账户；如该片发行收入不足以抵扣上述相关款项，甲方将从乙方支付的预付款中进行相应扣除，剩余预付款（如有）返还至乙方指定账户；如乙方预付款仍不足以抵扣上述相关款项，乙方应在收到甲方的书面通知之日起的五个工作日内补齐差额。逾期不补的，每逾一日，乙方应向甲方支付应付而未付金额的5‰的违约金，直至应付而未付金额付清为止。\\n    八、该片的发行收入，在扣除甲方的成本、甲方应得的发行收益及进出口公司应得的票房分成款后余额支付至乙方指定账户，账户信息如下:\\n     开户名称：北京耀莱影视文化传媒有限公司\\n     开户银行：交通银行北京东区支行\\n     账    号：110060973018010097703\\n     乙方应于甲方付款前向甲方开具乙方应得收入等额的合法发票，若乙方未按约定交付发票，甲方有权延迟履行付款义务，且不承担任何违约责任。\\n九、若乙方逾期支付预付款达3个工作日，甲方有权单方解除双方签订的《进口买断影片<火力全开>协助推广合同》及本协议，并按预付款的5‰/日追究乙方的违约责任。\\n十、本协议的全部条款均系商业机密，未经甲方书面同意，乙方不得向任何第三方泄露协议内容。如乙方违反该项约定，应赔偿由此给甲方造成的一切损失。\\n    十一、本协议在履行过程中发生的争议，双方应友好协商解决，协商解决不成，任何一方均可向甲方住所地有管辖权的人民法院提起诉讼。\\n    十二、本协议与原合同具有同等法律效力，若本协议与原合同中条款有相冲突之处，以本协议为准。本协议一式肆份，甲乙双方各执贰份，具有同等法律效力，自双方加盖公章或合同专用章之日起生效。\"}, \"digital_fee\": {\"payway\": {\"pkdk\": \"1\"}, \"docs\": [{\"doc_id\": \"3010\", \"doc_type\": \"12\", \"origin_name\": \"火力全开 数字制作费用清单.JPG\", \"doc_name\": \"1ho71rusel6bgujdi.jpeg\", \"thumbs\": [\"1ho71rusel6bgujdi.jpeg\"], \"catalog\": \"prjdoc\", \"type\": \"image\"}]}}', '{\"imex_rate\":\"150\",\"advance_pay\":\"100000\",\"buyout_pay\":\"140000\",\"material_fee\":\"0\"}', 270000, 94735500, 0, '[\"崔松雪\"]', '{\"UID87\": \"崔松雪\"}', '崔松雪', 87, '[\"PF000042\"]', '{\"PF000042\": \"北京耀莱影视文化传媒有限公司\"}', '[{\"user_id\":\"11343\",\"user_name\":\"u5317u4eacu8000u83b1\"}]', '{\"653\":\"台湾美商宏声音乐有限公司台湾分公司\"}', '2017-08-17 00:00:00', '2018-08-17 00:00:00', '2017-10-20', 0, '2017-10-20 00:00:00', '2017-11-19 00:00:00', '[{\"start_time\":\"2017-10-20\",\"end_time\":\"2017-11-19\"}]', NULL, NULL, '[{\"doc_id\": \"3677\", \"doc_type\": \"6\", \"origin_name\": \"《火力全开》补充协议.pdf\", \"doc_name\": \"31gd8fqf2exvusxa.pdf\", \"thumbs\": [\"5bzti4q6uow6ulbz.jpeg\", \"xartx3ly0y5bqs6t.jpeg\", \"i4rhl12p32o4sagd.jpeg\", \"16ivtql5otuzs8d95.jpeg\"], \"catalog\": \"prjdoc\", \"type\": \"pdf\"}, {\"doc_id\": \"3901\", \"doc_type\": \"11\", \"origin_name\": \"《火力全开》中数合同盖章.pdf\", \"doc_name\": \"fmg0jb5piniy0q2e.pdf\", \"thumbs\": [\"9j9j6bjfn5tabfp2.jpeg\", \"1cnic2a99cb5pvnoh.jpeg\", \"icd4p9hdmvgg6ze.jpeg\", \"174sctvt0fbafnxga.jpeg\", \"b4mygkyb3oz6fnsg.jpeg\", \"127a9huhlio6m18bw.jpeg\", \"16u0ee9pcr6bvh1m4.jpeg\"], \"catalog\": \"prjdoc\", \"type\": \"pdf\"}, {\"doc_id\": \"3902\", \"doc_type\": \"13\", \"origin_name\": \"《火力全开》内贸合同.pdf\", \"doc_name\": \"1gjva4das8f9t16vj.pdf\", \"thumbs\": [\"s5uuva5emeq80vlf.jpeg\", \"7ecqr79pggcqimlo.jpeg\", \"1b3fgr2qder5o0gy8.jpeg\", \"6ovg3yyd1g7w4njs.jpeg\"], \"catalog\": \"prjdoc\", \"type\": \"pdf\"}]', '[{\"doc_id\": \"2083\", \"doc_type\": \"1\", \"origin_name\": \"火力全开 公映许可证.pdf\", \"doc_name\": \"87qdt13siqjwlsi1.pdf\", \"thumbs\": [\"197pvcwpptwxwsngc.jpeg\", \"17rw9ftkt4gwyvpz3.jpeg\"], \"catalog\": \"prjd