INSERT INTO `fdm_project` VALUES (1503643425108, 1, 'M2017020', 2017020, 1, 1, 2, 0, 1, '第一夫人', 0, 0, '{\"remark\":\"\"}', '{\"settlement_time\": {\"start_time\": \"2018-01-19\", \"months\": \"3\"}, \"settlement_object\": [{\"index\": \"1\", \"object_name\": \"结算对象一\", \"bank\": \"招商银行上海分行泰兴支行\", \"account_name\": \"上海熙颐文化传媒有限公司 \", \"bank_account\": \"121919319410801 \", \"remark\": \"\"}], \"service_fee\": {\"boxoffice_type\": \"总票房\", \"distribute_object\": \"华夏分成\", \"function_name\": \"huaxia_split_distribution\", \"data\": {\"split_amount\": \"100000000\", \"left_rate\": \"1000\", \"right_rate\": \"1200\"}}, \"agent_fee\": {\"contract_text\": \"二、甲方负责向相关公司支付乙方预付的该片版权和素材购买费用、相关引进费用、译制费用、票房分成款预付款、送审费。甲方负责该片声画合成、数字母版制作、硬盘复制及发运，费用由乙方承担。乙方承担全部宣传推广、发行差旅等费用。\\n三、该片在院线、影院的发行收入以及总政、武警、数字流动放映等的销售收入全部汇入甲方账户。该片在院线、影院发行的收入分配方式按本协议第六条分配，总政、武警、数字流动放映等的销售收入按甲方得10%，乙方得90%进行分配。\\n四、乙方应在与甲方签订原合同之日起2个工作日内，一次性向甲方预付如下款项：\\n版权费、各项税金、对外付款手续费、票房分成款预付款、译制费、声画合成费、数字母版制作费、硬盘复制及发运费用（见“《第一夫人》发行预算”），共计人民币玖拾伍万元整（￥950,000.00）（以下简称“预付款”）支付至甲方的指定账户，账户信息如下：\\n开户名称：华夏电影发行有限责任公司\\n    开户银行：中国建设银行北京北太平庄支行\\n账    号：1100 1016 5000 5300 4211 \\n乙方逾期支付上述款项的，每逾一日，乙方应向甲方支付应付而未付金额的5‰的违约金。\\n    五、甲、乙双方同意，双方就该片的投入以各自加盖公司公章的结算清单及发票原件为准。\\n六、甲、乙双方同意，该片的院线、影院放映票房（以下简称“总票房”或“票房总收入”）的发行收入按以下方式分配：\\n     1、当该片总票房在人民币1亿元以内（含1亿元）时，甲方收取总票房的10%；\\n     2、当该片总票房大于人民币1亿元时，甲方再收取超出1亿元部分票房的12%。  \\n   七、甲、乙双方确认，中国电影集团公司电影进出口分公司（以下简称“进出口公司”）获得该片票房总收入1.5%的分成。\\n    八、甲、乙双方同意，根据本协议第三条约定汇入甲方的发行收入按以下顺序回收和分配：\\n（1）首先抵扣甲方的成本及甲方应得的发行收益；\\n（2）再扣除进出口公司应得的票房分成款；\\n（3）剩余发行收入支付至乙方指定的银行账户。\\n    若该片的发行收入足以抵扣甲方的成本、甲方应得的发行收益及进出口公司应得的票房分成款等相关款项，甲方返还乙方支付的预付款至乙方指定账户；如该片发行收入不足以抵扣上述相关款项，甲方将从乙方支付的预付款中进行相应扣除，剩余预付款（如有）返还至乙方指定账户；如乙方预付款仍不足以抵扣上述相关款项，乙方应在收到甲方的书面通知之日起的五个工作日内补齐差额。逾期不补的，每逾一日，乙方应向甲方支付应付而未付金额的5‰的违约金，直至应付而未付金额付清为止。\\n    九、该片的发行收入，在扣除甲方的成本、甲方应得的发行收益及进出口公司应得的票房分成款后余额支付至乙方指定账户，账户信息如下:\\n开户名称：上海熙颐文化传媒有限公司 \\n开户银行：招商银行上海分行泰兴支行\\n账    号：121919319410801 \\n乙方应于甲方付款前向甲方开具乙方应得收入等额的合法发票，若乙方未按约定交付发票，甲方有权延迟履行付款义务，且不承担任何违约责任。\\n    十、若乙方逾期支付预付款达3个工作日，甲方有权单方解除双方签订的《进口买断影片<第一夫人>协助推广合同》及本协议，并按预付款的5‰/日追究乙方的违约责任。\"}, \"digital_fee\": {\"payway\": {\"pkdk\": \"1\"}, \"sum_digital_fee_1\": \"11640000\", \"sum_digital_fee_2\": \"14000000\", \"sum_digital_fee_3\": 0, \"docs\": [{\"doc_id\": \"4620\", \"doc_type\": \"12\", \"origin_name\": \"第一夫人数字制作费用清单（2018）.JPG\", \"doc_name\": \"11gyju3ef5k6101wt.jpeg\", \"thumbs\": [\"11gyju3ef5k6101wt.jpeg\"], \"catalog\": \"prjdoc\", \"type\": \"image\"}], \"report_total_fee\": \"19900000\", \"report_detail\": [{\"fee_name\": \"母版制作-进口片JPEG2K 2D 第二版起\", \"unit\": \"个\", \"price\": 2000000, \"number\": \"1\", \"amount\": 2000000, \"remark\": \"\"}, {\"fee_name\": \"2D全片对白字幕\", \"unit\": \"个\", \"price\": 1500000, \"number\": \"1\", \"amount\": 1500000, \"remark\": \"\"}, {\"fee_name\": \"片头、尾字幕\", \"unit\": \"个\", \"price\": 150000, \"number\": \"2\", \"amount\": 300000, \"remark\": \"\"}, {\"fee_name\": \"进口片声画合成\", \"unit\": \"个\", \"price\": 700000, \"number\": \"1\", \"amount\": 700000, \"remark\": \"\"}, {\"fee_name\": \"DVD母盘\", \"unit\": \"个\", \"price\": 150000, \"number\": \"2\", \"amount\": 300000, \"remark\": \"\"}, {\"fee_name\": \"硬盘\", \"unit\": \"个\", \"price\": 100000, \"number\": \"1\", \"amount\": 100000, \"remark\": \"\"}, {\"fee_name\": \"母版制作-进口片JPEG2K 2D 第一版\", \"unit\": \"个\", \"price\": 1000000, \"number\": \"1\", \"amount\": 1000000, \"remark\": \"\"}, {\"fee_name\": \"硬盘复制费-2D打包\", \"unit\": \"块\", \"price\": 14000000, \"number\": \"1\", \"amount\": 14000000, \"remark\": \"\"}]}}', '{\"imex_rate\":\"150\",\"advance_pay\":\"200000\",\"buyout_pay\":\"140000\",\"material_fee\":\"0\"}', 5900000, 14000000, 0, '[\"崔松雪\"]', '{\"UID87\": \"崔松雪\"}', '崔松雪', 87, '[\"PF000358\"]', '{\"PF000358\": \"上海熙颐文化传媒有限公司\"}', '[{\"user_id\":\"8997\",\"user_name\":\"u7199u9890u6587u5316\"}]', '{\"858\":\"智利寓言影业\",\"859\":\"法国野蛮人群电影工作室\",\"860\":\"美国LD娱乐影片公司\"}', '2017-10-31 00:00:00', '2019-10-31 00:00:00', '2018-01-19', 0, '2018-01-19 00:00:00', '2018-02-18 00:00:00', '[{\"sta